package cryptox

import (
	"crypto"
	"encoding/base64"
	"fmt"
	"io/ioutil"
	"testing"
)

type rsaTest struct {
	out       string
	in        string
	halfState string // marshaled hash state after first half of in written, used by TestGoldenMarshal
}

func TestRsa(t *testing.T) {
	privateKey, _ := ioutil.ReadFile("D:/log/rsa/key/private.pem")
	publicKey, _ := ioutil.ReadFile("D:/log/rsa/key/public.pem")
	fmt.Println(privateKey)
	fmt.Println(publicKey)
	private, _ := NewRsaPrivateCipher(privateKey, false)
	public, _ := NewRsaPublicCipher(publicKey)
	origData := []byte("abcda")
	crypted, _ := public.Encrypt(origData)
	oriData, _ := private.Decrypt(crypted)
	fmt.Println(len(crypted))
	fmt.Println(base64.StdEncoding.EncodeToString(crypted))
	fmt.Println(string(oriData))
}

func TestRsaLong(t *testing.T) {
	privateKey, _ := ioutil.ReadFile("D:/log/rsa/key/private.pem")
	publicKey, _ := ioutil.ReadFile("D:/log/rsa/key/public.pem")
	fmt.Println(privateKey)
	fmt.Println(publicKey)
	private, _ := NewRsaPrivateCipher(privateKey, false)
	public, _ := NewRsaPublicCipher(publicKey)
	origData := []byte("")
	fmt.Println(len(origData))
	crypted, e1 := public.Encrypt(origData)
	oriData, e2 := private.Decrypt(crypted)
	fmt.Println(base64.StdEncoding.EncodeToString(crypted))
	fmt.Println(len(oriData), string(oriData))
	fmt.Println(e1, e2)
}

func TestSign(t *testing.T) {
	privateKey, _ := ioutil.ReadFile("D:/log/rsa/key/private.pem")
	publicKey, _ := ioutil.ReadFile("D:/log/rsa/key/public.pem")
	private, _ := NewRsaPrivateCipher(privateKey, false)
	public, _ := NewRsaPublicCipher(publicKey)
	origData := []byte("abcda")
	signed, _ := private.Sign(origData, crypto.MD5)
	fail := public.VerySign(origData, signed, crypto.MD5)
	fmt.Println(origData)
	fmt.Println(signed)
	fmt.Println(fail)
}
